<template>
  <div id="drag" ref="drag" :style="drag" class="drag">
    <div id="drag_bg" ref="drag_bg" :style="drag_bg_style" class="drag_bg"/>
    <div ref="drag_text" :style="drag_text_style" class="drag_text">{{ confirmWords }}</div>
    <div
      id="handler"
      ref="handler"
      :class="handler_class"
      :style="handler_style"
      @mousedown="mousedownFn($event)"/>
  </div>
</template>

<script>
export default {
  name: 'Hua',
  components: {},
  props: {},
  data() {
    return {
      beginClientX: 0, /* 距离屏幕左端距离*/
      mouseMoveStata: false, /* 触发拖动状态  判断*/
      maxwidth: 218, /* 拖动最大宽度，依据滑块宽度算出来的  滑块宽度-42PX  */ // 258-60
      confirmWords: '拖动滑块验证', /* 滑块文字*/
      confirmSuccess: false, /* 验证成功判断*/
      handler_class: {
        handler: true,
        handler_bg: true,
        handler_ok_bg: false
      },
      handler_style: {
        left: 0
      },
      drag_bg_style: {
        width: 0
      },
      drag_text_style: {
        width: 260
      },
      drag: {
        color: '#333'
      },
      events: {
        handleEvent: function(event) {
          switch (event.type) {
            case 'mousemove':
              this.myMouseMove(event)
              break
            case 'mouseup':
              this.myMouseUp(event)
              break
          }
        },
        myMouseMove: (e) => {
          if (this.mouseMoveStata) {
            var width = e.clientX - this.beginClientX
            if (width > 0 && width <= this.maxwidth) {
              this.$refs.handler.style.left = width + 'px'
              this.$refs.drag_bg.style.width = width + 'px'
            } else if (width > this.maxwidth) {
              this.successFunction()
            }
          }
        },
        myMouseUp: (e) => {
          this.mouseMoveStata = false
          const width = e.clientX - this.beginClientX
          if (width < this.maxwidth) {
            this.$refs.handler.style.left = '0px'
            this.$refs.drag_bg.style.width = '0px'
          }
        }
      }
    }
  },
  mounted() {
    var drag = document
    drag.addEventListener('mousemove', this.events, false)
    drag.addEventListener('mouseup', this.events, false)
  },
  methods: {
    mousedownFn: function(e) {
      this.mouseMoveStata = true
      this.beginClientX = e.clientX
    },
    successFunction() {
      // 成功回调
      // console.log("成功回调--");
      this.handler_class.handler_bg = false
      this.handler_class.handler_ok_bg = true
      this.confirmWords = '验证通过'
      this.drag.color = '#fff'
      this.$refs.handler.style.left = this.maxwidth + 'px'
      this.$refs.drag_bg.style.width = this.maxwidth + 'px'

      const btn2 = document
      btn2.removeEventListener('mousemove', this.events, false)
      btn2.removeEventListener('mouseup', this.events, false)

      // 把文字宽度改为230px，让它居中
      this.$refs.drag_text.style.width = 230 + 'px'
      this.confirmSuccess = true
      this.$emit('verify_pass')
    }, // 验证成功函数
    // 重置验证码状态
    reset_verify() {
      console.log('开始重置验证码状态')
      this.handler_class.handler_bg = true
      this.handler_class.handler_ok_bg = false
      this.confirmWords = '拖动滑块验证'
      this.drag.color = '#333'
      this.$refs.handler.style.left = '0px'
      this.$refs.drag_bg.style.width = '0px'
      this.mouseMoveStata = false

      const btn2 = document
      btn2.addEventListener('mousemove', this.events, false)
      btn2.addEventListener('mouseup', this.events, false)

      // 把文字宽度改为260px，让它居中
      this.$refs.drag_text.style.width = '260px'
    },

    // 绑定的滑块拖动事件。
    mouseMove_event(ev) {
      if (this.mouseMoveStata) {
        console.log('滑块拖动')
        const width = ev.clientX - this.beginClientX
        console.log('width--' + width)
        if (width > 0 && width <= this.maxwidth) {
          this.$refs.handler.style.left = width + 'px'
          this.$refs.drag_bg.style.width = width + 'px'
        } else if (width > this.maxwidth) {
          this.successFunction()
        }
      }
    },
    // 绑定的鼠标松开事件。
    mouseUp_event(ev) {
      console.log('鼠标松开')
      this.mouseMoveStata = false
      const width = ev.clientX - this.beginClientX
      // let width = document.getElementById("drag_bg").offsetWidth - this.beginClientX;
      console.log('width--' + width)
      if (width < this.maxwidth) {
        this.$refs.handler.style.left = '0px'
        this.$refs.drag_bg.style.width = '0px'
      }
    }
  }
}
</script>

<style scoped>
    .drag {
        display: inline-block;
        position: relative;
        background-color: #e8e8e8;
        width: 260px;
        height: 34px;
        margin-left: 30px;
        /*margin-top: 100px;*/
        line-height: 34px;
        text-align: center;
        /*禁止选中*/
        user-select: none;
    }

    .handler {
        position: absolute;
        top: 0px;
        left: 0px;
        width: 43px;
        height: 34px;
        border: 1px solid #ccc;
        cursor: move;
    }

    .handler_bg {
        background: #fff url("data:image/png;base64,") no-repeat center;
    }

    .handler_ok_bg {
        background: #fff url("data:image/png;base64,") no-repeat center;
    }

    .drag_bg {
        background-color: #7ac23c;
        height: 34px;
        width: 0px;
    }

    .drag_text {
        position: absolute;
        top: 0px;
        width: 260px;
        -moz-user-select: none;
        -webkit-user-select: none;
        user-select: none;
        -o-user-select: none;
        -ms-user-select: none;
    }
</style>
